const AnotherPackage = require('../index');

describe('AnotherPackage', () => {
  let pkg;

  beforeEach(() => {
    pkg = new AnotherPackage();
  });

  test('should create instance with default name', () => {
    expect(pkg.name).toBe('another-package');
    expect(pkg.version).toBe('2.1.0');
  });

  test('should create instance with custom name', () => {
    const customPkg = new AnotherPackage('custom-name');
    expect(customPkg.name).toBe('custom-name');
  });

  test('should return correct message', () => {
    expect(pkg.getMessage()).toBe('Greetings from another-package');
  });

  test('should process array correctly', () => {
    const input = [3, 1, 2, 1, 3];
    const result = pkg.processArray(input);
    expect(result).toEqual([1, 2, 3]);
  });

  test('should return info object', () => {
    const info = pkg.getInfo();
    expect(info.name).toBe('another-package');
    expect(info.version).toBe('2.1.0');
    expect(info.timestamp).toBeDefined();
  });
});